package com.syntax.class02;

public class NonPrimitiveDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//String is just  1 example of non-primitive data type
		
		String name="Vera";
		String address="123 Flower Street";
		
		//String is a non-primitive data type, we put inside double quotes
		String age="17";
		
		//whenyou want to print, you can use shortcut start typing syso+ctrl+Space and it fills out the rest
		
		
		System.out.println(name);
		
		System.out.println("My name is " + name);
		
		System.out.println(name+" is "+age);
		
		String fruit = "apple";
		double price=1.99;
		
		System.out.println("The price of the "+fruit+" is "+price);
		
		fruit="mango";
	    price=5.99;
	    
	    System.out.println("The price of the "+fruit+" is "+price);
	    
	    
	    
	    
				
		
	}

}
