package com.syntax.class02;

public class Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String name="Vera";
		String LastName ="Yusifov";
		String grade="Batch 14";
		String city="Herndon";
		String state="VA";
		String PhoneNumber="555 555 5555";
		
		System.out.println("My name is "+name+" "+LastName+".");
		System.out.println("I am a "+grade+" student.");
		System.out.println("I live in "+city+", "+state+"");
		System.out.println("My phone number is "+PhoneNumber+".");
		
		grade="Batch 15";
		city="Prague";
		state="Czech Republic";
		PhoneNumber="111 111 1111";
		
		System.out.println("My name is "+name+" "+LastName+".");
		System.out.println("I am a "+grade+" student.");
		System.out.println("I moved to "+city+", "+state+"");
		System.out.println("My new phone number is "+PhoneNumber+".");
		
		
		
	}
	

}
