package com.syntax.class03;

public class evenVsOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number=67;
		
		
		if(number%2==0) {
			System.out.println("Number "+number+ "is even");
		}
		else {
			System.out.println("Number "+number+ " is odd");
		}
	}

}
