package com.syntax.class07;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int i=1; i<=5; i++) {
	System.out.println("Hello");
}
System.out.println("------------------");

//print numbersfrom 1 to 20

for(int i=1; i<=20;i++) {
	System.out.print(i+" ");
}
System.out.println("------------------");
for(int i=30; i>=10; i-- ) {
	System.out.print(i+" ");
}
System.out.println("------------------");
for(int i=0; i<=10;i+=3) {
	System.out.print(i+" ");
}
	}

}
