package com.syntax.class07;

public class LoopHW1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 20;
		while(i<=50) {
		if(i%2==1) {
			System.out.print(i+" ");
		}
		i++;
		}
		System.out.println("\n");
		
		int a=21;
		while(a<=50) {
			System.out.print(a+" ");
	
		a+=2;
	}
	}
}