package com.syntax.class07;

public class WhileLoopDemo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// print allnumbers from 1 to 10
		int num = 1;
		while (num < 10) {
			System.out.print(num + " ");
			num++;
		}
		System.out.println("--------------------");

		int a = 10;
		while (a <= 25) {
			System.out.print(a + " ");
			a++;
		}
		System.out.println("------------");
		int b = 10;
		while (b>=1) {
			System.out.print(b+" ");
			b--;

		}
	}
}
